import React  from "react";


 import { Data } from "./data";

 import * as XLSX from "xlsx";
 import * as xlsx from "xlsx";

class ExcelToJson extends React.Component {
  constructor(props) {
    super(props);
    this.handleClick = this.handleClick.bind(this);
    this.state = {
      file: "",
      dataInFile:[]
    };
  }

  handleClick(e) {
    this.refs.fileUploader.click();
  }

  filePathset(e) {
    e.stopPropagation();
    e.preventDefault();
    var file = e.target.files[0];
    console.log(file);
    this.setState({ file });

    console.log(this.state.file);
  }

  readFile() {
    var f = this.state.file;
    var name = f.name;
    const reader = new FileReader();
    reader.onload = (evt) => {
      // evt = on_file_select event
      /* Parse data */
      const bstr = evt.target.result;
      const wb = XLSX.read(bstr, { type: "binary" });
      /* Get first worksheet */
      const wsname = wb.SheetNames[0];
      const ws = wb.Sheets[wsname];
      /* Convert array of arrays */
      const data = XLSX.utils.sheet_to_csv(ws, { header: 1 });
      /* Update state */
      console.log("Data>>>" + data);// shows that excel data is read
      console.log(this.convertToJson(data)); // shows data in json format
      this.setState({
        dataInFile: this.convertToJson(data)
      })
    };
    reader.readAsBinaryString(f);
  }

  convertToJson(csv) {
    var lines = csv.split("\n");

    var result = [];

    var headers = lines[0].split(",");

    for (var i = 1; i < lines.length; i++) {
      var obj = {};
      var currentline = lines[i].split(",");

      for (var j = 0; j < headers.length; j++) {
        obj[headers[j]] = currentline[j];
      }

      result.push(obj);
    }

    var stingJson = JSON.stringify(result); //JSON
    return JSON.parse(stingJson)
  }

   readUploadFile = (e) => {
    e.preventDefault();
    if (e.target.files) {
        const reader = new FileReader();
        reader.onload = (e) => {
            const data = e.target.result;
            const workbook = xlsx.read(data, { type: "array" });
            const sheetName = workbook.SheetNames[0];
            const worksheet = workbook.Sheets[sheetName];
            const json = xlsx.utils.sheet_to_json(worksheet);
            console.log(json);
            this.setState({
              dataInFile: json
            })
        };
        reader.readAsArrayBuffer(e.target.files[0]);
    }
}


  render() {
    return (
      <div>
        <input
          type="file"
          id="file"
          ref="fileUploader"
          onChange={this.readUploadFile}
        />
        <button
          onClick={() => {
            this.readFile();
          }}
        >
          Read File
        </button>
        {JSON.stringify(this.state.dataInFile )}

        <br/>
        <br/>
        <br/>
        <br/>
        <table class="table table-striped">
                <thead>
                    <tr>
                    <th>id</th>
                    <th>Name</th>
                    <th>age</th>
                    </tr>
                </thead>
                <tbody>

        {/* {this.state.dataInFile[1]} */}

                {/* {this.state.dataInFile.map( info =>
                 (
                  <tr>
                  <td>{info.id}</td>
                  <td>{info.name}</td>
                  <td>{info.age}</td>
                  </tr>
                 )
                )}                     */}
                </tbody>
            </table>
      </div>
    );
  }
}

